html:
{%- macro add_header(page_num, max_pages, doc, letter_head, no_letterhead) -%}
   {%- if letter_head and not no_letterhead -%}
	<div class="letter-head">{{ letter_head }}</div>
   {%- endif -%}
{%- endmacro -%}
{{ add_header(0,1,doc,letter_head, no_letterhead) }}

<table border="0" cellspacing="0" cellpadding="0" width="100%">
    <tbody>
        <tr>
            <td width="50%" valign="top">
                <p><strong>Address</strong></p>
                <p> {{ doc.supplier_name }}<br>
	           {%- if doc.address_display -%}
                         {{ doc.address_display }} 
	           {%- endif -%}
                </p>
            </td>
            <td width="50%" valign="top" style="text-align: right; border: 1px solid black;">
                <p><strong>PO Number and Date:</strong><br>
                    <span style="font-size: x-small;">{{ doc.name }} dated {{ doc.transaction_date }}</span>
                </p>
                <p><strong>Contact Office/Telephone/Fax no.:</strong><br>
	           {%- if doc.contact_display -%}
                        <span style="font-size: x-small;"> {{ doc.contact_display }} /</span>
	           {%- endif -%}
	           {%- if doc.contact_mobile -%}
                        <span style="font-size: x-small;"> {{ doc.contact_mobile }} </span>
	           {%- endif -%}
                </p>
            </td>
        </tr>
    </tbody>
</table>
<p style="margin-top: 20px;">
    <strong>Subject:</strong> <u>{{ doc.title }}</u> <br><br>
    <strong>Quotation Reference:</strong> {{ doc.ref_sq }} dated {{ frappe.db.get_value("Supplier Quotation", doc.ref_sq, "transaction_date") }}
</p>

{%- if doc.po_header -%}
{{ doc.po_header }}
{%- endif -%}

<table class="table table-bordered" style="margin-top: 15px;">
  <tbody>
	<tr>
		<th>Sr</th>
		<th>Material Code</th>
		<th>Material Name</th>
		<th>Unit</th>
		<th>Qty</th>
		<th>Rate (Nu.)</th>
		<th class="text-right">Amount(Nu.)</th>
	</tr>
	{%- for row in doc.items -%}
	<tr>
		<td style="width: 3%;">{{ row.idx }}</td>
		<td style="width: 10%;">{{ row.item_code }}</td>			
		<td style="width: 45%;">{{ row.item_name }}</td>	
		<td style="width: 10%;">{{ row.uom }}</td>
		<td style="width: 10%;">{{ row.qty }}</td>
		<td style="width: 10%;">{{ row.rate }}</td>
		<td style="width: 12%;">{{ row.amount }}</td>
	</tr>
	{%- endfor -%}
        <tr><td></td><td colspan='5'><b>Total</b></td><td>{{ doc.total }}</td></tr>
  </tbody>
</table>

{%- if doc.terms -%}
{{ doc.terms }}
{%- endif -%}

{%- if doc.po_footer_text -%}
{{ doc.po_footer_text }}
{%- endif -%}
