html:
{%- macro add_header(page_num, max_pages, doc, letter_head, no_letterhead) -%}
   {%- if letter_head and not no_letterhead -%}
	<div class="letter-head">{{ letter_head }}</div>
   {%- endif -%}
{%- endmacro -%}
{{ add_header(0,1,doc,letter_head, no_letterhead) }}

<table border="0" cellspacing="0" cellpadding="0" width="100%">
    <tbody>
        <tr><td  width="50%" valign="top" rowspan=2>
                <p> Ms. {{ doc.customer_name }}<br></p>
                <p>Telephone/Fax: {{ frappe.db.get_value("Customer", doc.customer_name, "telephone_and_fax") }}</p>
                <p> {{ frappe.db.get_value("Customer", doc.customer_name, "location") }}</p>
</td><td  style="text-align: center;" bgcolor=#999><strong><span style="font-size: small;">Sales Order</span></strong></td></tr>
        <tr>
            <td width="50%" valign="top" style="text-align: left; border: 1px solid black;">
                <p><span style="font-size: small; font-weight: bold;">Sales Order Number and Date:</span><br>
                    <span style="font-size: small;">{{ doc.name }} dated {{ doc.transaction_date }}</span>
                </p>
                <p><span style="font-size: small; font-weight: bold;">Email/Telephone/Fax/Mobile.:</span><br>
	           {%- if doc.items -%}
                        <span style="font-size: small;"> 
{{ frappe.db.get_value("Warehouse", doc.items[0].warehouse, "email_id") }} / 
{{ frappe.db.get_value("Warehouse", doc.items[0].warehouse, "phone_no") }} /
{{ frappe.db.get_value("Warehouse", doc.items[0].warehouse, "mobile_no") }} </span>
	           {%- endif -%}
                </p>
            </td>
        </tr>
    </tbody>
</table>
<p style="margin-top: 20px;">
    <strong>Subject:</strong> <u>{{ doc.title }}</u> <br><br>
    <strong>Quotation Reference:</strong> 
{%- if doc.items -%}
{{ doc.items[0].prevdoc_docname }} dated {{ frappe.db.get_value("Quotation", doc.items[0].prevdoc_docname, "transaction_date") }}
{%- endif -%}
<br>
    <strong>Customer PO No:</strong> {{ doc.po_no }}
<br>
    <strong>Customer PO Date:</strong> {{ doc.po_date }}
<br><br>
</p>

{%- if doc.sales_order_header_text -%}
{{ doc.sales_order_header_text }}
{%- endif -%}

<table class="table table-bordered" style="margin-top: 15px;">
  <tbody>
	<tr style="font-size: 14px; font-weight: lighter; text-align: center;">
		<th style="text-align: center;">Sr</th>
		<th style="text-align: center;">Material Code</th>
		<th style="text-align: center;">Material Name</th>
		<th style="text-align: center;">Unit</th>
		<th style="text-align: center;">Qty</th>
		<th style="text-align: center;">Rate (Nu.)</th>
		<th class="text-right" style="text-align: center;">Amount(Nu.)</th>
	</tr>
	{%- for row in doc.items -%}
	<tr>
		<td style="width: 3%;">{{ row.idx }}</td>
		<td style="width: 10%;">{{ row.item_code }}</td>			
		<td style="width: 45%;">{{ row.item_name }}</td>	
		<td style="width: 10%;">{{ row.stock_uom }}</td>
		<td style="width: 10%;">{{ row.qty }}</td>
		<td style="width: 10%;">{{ row.rate }}</td>
		<td style="width: 12%;">{{ row.amount }}</td>
	</tr>
	{%- endfor -%}  
         {%- if doc.discount_amount -%}
        <tr><td></td><td colspan='5'>{{ doc.additional_cost_or_discount_description }}</td><td>{{ doc.discount_amount * -1 }}</td></tr>
         {%- endif -%}
        <tr><td></td><td colspan='5'><b>Total</b></td><td>{{ doc.net_total }}</td></tr>
  </tbody>
</table>

{%- if doc.terms -%}
<p><strong>Sales Terms and Conditions</strong></p><br />
{{ doc.terms }}
{%- endif -%}

{%- if doc.sales_order_footer_text -%}
{{ doc.sales_order_footer_text }}
{%- endif -%}
